<?php	$url=$_GET['url']?$_GET['url']:"";	//域名信息	$data=getinfo($url);		$replace=array(		'<div class="info">网站名:<b>$1</b>  域名年龄:<b class="strong">$2</b></font></td>  过期时间:<b>$3</b>',	);	//备案号	$preg="/>(.*到.*)<\/a>/Ui";	preg_match_all($preg,$data,$info);	//string strip_tags($info);	//print_r($info[1]);		foreach($info[1] as $v ){		$v=iconv('GB2312', 'UTF-8', $v);			$arr[strip_tags($v)]=strip_tags($v);	}		print_r($arr);		exit;	$beian=$info['bei'][1]?" 备:".$info['bei'][1]:"";	$base=preg_replace($preg,$replace,$data);	if($data==$base){		$html .= "网站无法访问！ ";		$whoisurl="http://whois.chinaz.com/".$domain;		$whois=getinfo($whoisurl);			$preg="/(<br\/>)?([^\d]{2,6}时间.*)<br\/>/Ui";		preg_match_all($preg,$whois,$info['whois']);		$html.="<a href=".$whoisurl." target='_blank'>whois</a> " . implode(" | ",$info['whois'][2]);					}else{		$str.=$base.$beian;	}	//百度结果	$gburl="http://www.baidu.com/s?wd=".$front;	$data1=getinfo($gburl);	preg_match("/百度为您找到相关结果(.*?)个/si",$data1,$info[baidu]);		$str.="<a href='{$gburl}' target='_blank'>[$front]百度结果".$info[baidu][1]."个</a>";		//百度seo	$azurl="http://baidurank.aizhan.com/baidu/".$domain."/";	$data2=getinfo($azurl);	preg_match_all("/<td class=\"tablehead\".*?>(.*?)<\/td>\n<td.*?>(.*?)<\/td>/si",$data2,$info[baidu]);	$baidu1=$info[baidu][1];	$baidu2=preg_replace("/<div id=\"abox.*?<\/div>/is","",$info[baidu][2]);		foreach($baidu1 as $k=>$v){		if($k<3){		$bdseo.=$v.$baidu2[$k];		}	}		echo "<span class=m><a href='http://{$domain}' target='_blank'>" . $domain ."</a></span>".$str.$html;	echo "<div class='info2'>".$bdseo."</div>";		function getinfo($url){	//获取内容$headers = array("Connection: keep-alive","Cache-Control: max-age=0","Accept: application/xml,application/xhtml+xml,text/html;q=0.9,text/plain;q=0.8,image/png,*/*;q=0.5","User-Agent: Mozilla/5.0 (Windows; U; Windows NT 5.1; en-US)","Avail-Dictionary: xdC_A6dv","Accept-Language: zh-CN,zh;q=0.8","Accept-Charset: utf-8;GBK,q=0.7,*;q=0.3"		);		$cookie_file=dirname(__FILE__).'/cookie.txt';		$ch = curl_init();		curl_setopt ($ch,CURLOPT_URL, $url);		curl_setopt ($ch,CURLOPT_RETURNTRANSFER, 1); 		curl_setopt ($ch,CURLOPT_CONNECTTIMEOUT,10); 	//	curl_setopt($ch,CURLOPT_POST,1);	//	curl_setopt($ch,CURLOPT_POSTFIELDS,$curlPost);		curl_setopt($ch,CURLOPT_HTTPHEADER, $headers);		curl_setopt($ch,CURLOPT_COOKIEJAR,$cookie_file);		curl_setopt($ch,CURLOPT_COOKIEFILE,$cookie_file);			//引入cookie 文件		$newfile = curl_exec($ch);		return $newfile;}?>